import React from 'react';
import {useSelector} from "react-redux";

const FilmsPage = () => {
    // const films = useSelector(state => state.movies.films)
    const films = [
        {
            title: 'Jungle Cruise',
            medium_cover_image: "https://yts.mx/assets/images/movies/jungle_cruise_2021/medium-cover.jpg",
            description_full: "Based on Disneyland\'s theme park ride where a small riverboat takes a group of travelers through " +
                "a jungle filled with dangerous animals and reptiles but with a supernatural element.",
            genres: ["Action", "Adventure", "Comedy"],
            year: 2021
        }

    ]
    return (
        <div>
            {films.map(film =>
                <div className='container'>
                    <div className='col'>
                        <img src={film.medium_cover_image}/>
                    </div>
                    <div>
                        <div>{film.title}</div>
                        <div>{film.genres}</div>
                        <div>{film.year}</div>
                        <div>{film.description_full}</div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default FilmsPage;
