import React, {useEffect} from 'react';
import {useDispatch, useSelector} from "react-redux";
import './pages.css'
import axios from "axios";
import {setCurrentPage, setMovies, setTotalCount} from "../redux/films-reducer";
const Pagination = () => {
    const dispatch = useDispatch();
    const tolalCount = useSelector(state => state.movies.totalCount);
    const pageSize = useSelector(state => state.movies.pageSize);
    const currentPage = useSelector(state => state.movies.currentPage);

    const pageCount = Math.ceil(tolalCount / pageSize)
    const pages = [];

    for (let i = 0; i <= pageCount; i++) {
        pages.push(i + 1)
    }


    const onPages = (pageNumber) => {
        dispatch(setCurrentPage(pageNumber))
        dispatch(setMovies())
        axios.get(`https://yts.mx/api/v2/list_movies.json?limit=${tolalCount}&page=${pageSize}`)
            .then(response => {
                dispatch(setMovies(response.data.data))
                dispatch(setTotalCount(response.data.data.movie_count))
            })
    }
    return (

            <div>
            {pages.map(p => {
                return <span className={currentPage === p && 'pages'}
                onClick={(e) => onPages(p)}>{p}</span>})}
            </div>

    );
};

export default Pagination;
