import React, {useEffect, useState} from 'react';
import './filmsPage.css'
import {useParams} from "react-router-dom";
import {useDispatch} from "react-redux";

import axios from "axios";
const FilmsPage = () => {
    const [films,setFilms] = useState({info:[]})
    const {id} = useParams();

    useEffect(() => {
        axios.get(`https://yts.mx/api/v2/list_movies.json/` + id)
            .then(response => {
                setFilms(response.data.data.movies)

            })
    },[])
    console.log(films)
    return (
        <div>
            {films.info.map(film =>
                <div className='container'>
                    <div >
                        <img src={film.medium_cover_image}/>
                    </div>
                    <div >
                        <div>{film.title}</div>
                        <div>{film.genres}</div>
                        <div>{film.year}</div>
                        <div>{film.description_full}</div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default FilmsPage;
