import React, {useState} from 'react';
import './filmsPage.css'
import {useParams} from "react-router-dom";
const FilmsPage = () => {
    // const films = useSelector(state => state.movies.films)
    const [films,setFilms] = useState({info:[]})
    const {id} = useParams();
    console.log(id)


    return (
        <div>
            {films.map(film =>
                <div className='container'>
                    <div >
                        <img src={film.medium_cover_image}/>
                    </div>
                    <div >
                        <div>{film.title}</div>
                        <div>{film.genres}</div>
                        <div>{film.year}</div>
                        <div>{film.description_full}</div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default FilmsPage;
