import axios from "axios";

const SET_MOVIES = 'SET-MOVIES',
      SET_CURRENT_PAGE = 'SET-CURRENT-PAGE',
      SET_TOTAL_COUNT = 'SET-TOTAL-COUNT'

let initialState = {
    films: [],
    pageSize: 2,
    totalCount: 33875,
    currentPage: 1
}


export default function filmsReducer (state  = initialState, action) {

    switch (action.type) {
        case SET_MOVIES :
            return {...state, films: action.films}
        case SET_CURRENT_PAGE:
            return {...state, currentPage: action.currentPage}
        case SET_TOTAL_COUNT:
            return {...state, totalCount: action.totalCount}
        default:
            return state
    }
}

export const setMovies = (films) => ({type: SET_MOVIES, films});
export const setCurrentPage = (currentPage) => ({type: SET_CURRENT_PAGE, currentPage})
export const setTotalCount = (totalCount) => ({type: SET_TOTAL_COUNT, totalCount})

export const getMovies = () => async (dispatch) => {
    axios.get('https://yts.mx/api/v2/list_movies.json?limit=10')
        .then(response => {
            dispatch(setMovies(response.data.data.movies))
            console.log(response.data.data.movie_count)
        })

}
export const getPagination = (tolalCount,currentPage) => async (dispatch) => {

    axios.get(`https://yts.mx/api/v2/list_movies.json?&page=${currentPage}&movie_count=${tolalCount}`)
        .then(response => {
            dispatch(setMovies(response.data.data.movies))
            dispatch(setTotalCount(response.data.data.movie_count))
            console.log(response.data.data.movie_count)
        })

}
